mapper.select:
select tag_id
from blog.article_tag
where article_id = #{id}

select
id, title, content, summary, category_id, thumbnail, is_top, status, view_count, is_comment, create_by,
create_time, update_by, update_time, del_flag
from article


        and id = #{id}


        and title = #{title}


        and content = #{content}


        and summary = #{summary}


        and category_id = #{categoryId}


        and thumbnail = #{thumbnail}


        and is_top = #{isTop}


        and status = #{status}


        and view_count = #{viewCount}


        and is_comment = #{isComment}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and del_flag = #{delFlag}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from article


        and id = #{id}


        and title = #{title}


        and content = #{content}


        and summary = #{summary}


        and category_id = #{categoryId}


        and thumbnail = #{thumbnail}


        and is_top = #{isTop}


        and status = #{status}


        and view_count = #{viewCount}


        and is_comment = #{isComment}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and del_flag = #{delFlag}
